package br.com.qrole.main.view.activities;

import android.content.Intent;
import android.os.Bundle;
import android.support.v4.view.MenuItemCompat;
import android.support.v7.app.AppCompatActivity;
import android.support.v7.widget.SearchView;
import android.view.Menu;
import android.view.MenuItem;
import android.view.View;
import android.widget.AdapterView;
import android.widget.ListView;

import java.util.ArrayList;
import java.util.Date;
import java.util.List;

import br.com.qrole.main.R;
import br.com.qrole.main.entities.Role;
import br.com.qrole.main.view.adapter.RoleAdapter;

public class MainScreenActivity extends AppCompatActivity implements SearchView.OnQueryTextListener {

    private RoleAdapter roleAdapter;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main_screen_activity);

        ListView listView = (ListView) findViewById(R.id.list_role);
        listView.setEmptyView(findViewById(R.id.empty_view_role));

        roleAdapter = new RoleAdapter(this, buildSomeFakeRoles());

        listView.setAdapter(roleAdapter);

        listView.setOnItemClickListener(new AdapterView.OnItemClickListener() {
            @Override
            public void onItemClick(AdapterView<?> adapterView, View view, int i, long l) {
                Role selectedRole = roleAdapter.getItem(i);

                Bundle bundle = new Bundle();
                bundle.putSerializable("entity", selectedRole);

                Intent intent = new Intent(MainScreenActivity.this, RoleViewerActivity.class);
                intent.putExtras(bundle);

                startActivity(intent);
            }
        });
    }

    @Override
    public boolean onCreateOptionsMenu(Menu menu) {
        getMenuInflater().inflate(R.menu.menu_search, menu);

        MenuItem searchItem = menu.findItem(R.id.search);
        SearchView searchView = (SearchView) MenuItemCompat.getActionView(searchItem);
        searchView.setOnQueryTextListener(this);

        return true;
    }

    private List<Role> buildSomeFakeRoles() {
        List<Role> roles = new ArrayList<>();

        for (int i = 1; i <= 15; i++) {
            Role r = new Role();
            r.setID(i);
            r.setTitle("Rolê número " + i);
            r.setDescription("Apenas uma descrição stub do role número " + i +
                    "\nTestando quebra de linha lorem ipsum sir dolor meu blablabla");

            if (i % 2 == 0) {
                r.setImage("");
            }

            r.setRoleDateTime(new Date());
            r.setAddress("Rua Dos testes número " + i + " bairro santa joana dos testes, " +
                    "São Paulo - Brasil");

            if (i == 10) {
                r.setDescription(r.getDescription() +
                        "\nDescrição grande para testar o Scroll: " + r.getDescription()
                        + r.getDescription() + r.getDescription() + r.getDescription() + r.getDescription());
            }

            roles.add(r);
        }

        return roles;
    }

    @Override
    public boolean onQueryTextSubmit(String query) {

        roleAdapter.doFilter(query);

        return true;
    }

    @Override
    public boolean onQueryTextChange(String newText) {
        return false;
    }
}
